mapper.select:
SELECT DISTINCT t.id, t.parent_id AS parentId, t.enterprise_id AS enterpriseId, t.department_code AS departmentCode, t.department_name AS departmentName, t.create_time AS createTime, t.update_time AS updateTime, t.create_user AS createUser, t.update_user AS updateUser, t.parameter1, t.parameter2, t2.enterprise_name AS enterpriseName FROM t_enterprise_department t LEFT JOIN t_enterprise t2 ON t.enterprise_id= t2.id LEFT JOIN t_sys_user t3 on t2.id=t3.enterprise_id LEFT JOIN t_sys_user_enterprise t4 on t3.id=t4.user_id
t.id=#{param.id}
and t.enterprise_id=#{param.enterpriseId}
and t.department_code=#{param.departmentCode}
and t.department_name like '%${param.departmentName}%'
and t2.enterprise_name like '%${param.enterpriseName}%'
and t3.id=#{param.userId}
order by t.department_code

SELECT DISTINCT t.id, t.parent_id, t.enterprise_id, t.department_code, t.department_name, t.create_time, t.update_time, t.create_user, t.update_user, t.parameter1, t.parameter2, t2.id as unitId, t2.enterprise_name FROM t_enterprise_department t LEFT JOIN t_enterprise t2 ON t.enterprise_id= t2.id LEFT JOIN t_sys_user t3 on t2.id=t3.enterprise_id LEFT JOIN t_sys_user_enterprise t4 on t3.id=t4.user_id
t.id=#{id}
and t.enterprise_id=#{enterpriseId}
and t.department_code=#{departmentCode}
and t.department_name like '%${departmentName}%'
and t2.enterprise_name like '%${enterpriseName}%'
and t3.id=#{userId}
order by t.department_code
