mapper.select:
SELECT DISTINCT t.id, t.business_license_number AS businessLicenseNumber, t.enterprise_code AS enterpriseCode, t.enterprise_name AS enterpriseName, t.industry_code AS industryCode, t.area_code AS areaCode, t.enterprise_type AS enterpriseType, t.telephone, t.email, t.zip_code AS zipCode, t.legal_person AS legalPerson, t.main_person AS mainPerson, t.main_person_mobile AS mainPersonMobile, t.map_x AS mapX, t.map_y AS mapY, t.map_z AS mapZ, t.address AS address, t.status, t.create_time AS createTime, t.update_time AS updateTime, t.create_user AS createUser, t.update_user AS updateUser, t4.var_name AS areaName, t5.var_name AS industryName FROM t_enterprise t LEFT JOIN t_sys_user t2 ON t.id = t2.enterprise_id LEFT JOIN t_sys_dic t4 ON t.area_code = t4.id LEFT JOIN t_sys_dic t5 ON t.industry_code = t5.id
t.id=#{ew.enterpriseId}
and t.enterprise_code=#{ew.enterpriseCode}
and t.enterprise_type=#{ew.enterpriseType}
and t.enterprise_name like '%${ew.enterpriseName}%'
and t.area_code=#{ew.areaCode}
and t.industry_code=#{ew.industryCode}
and t2.id=#{ew.userId}

