mapper.select:
select t.id, t.role_id, t.resource_id, t2.id AS roleId, t2.name AS roleName, t3.id AS resourceId, t3.name AS resourceName from t_sys_role_resource t inner join t_sys_role t2 on t.role_id=t2.id inner join t_sys_resource t3 on t.resource_id=t3.id
t.id=#{params.id}
and t.role_id=#{params.roleId}
and t.resource_id=#{params.resourceId}
